import React, { useState } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [query, setQuery] = useState("");
  const [severity, setSeverity] = useState("");
  const [attackType, setAttackType] = useState("");
  const [results, setResults] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const searchIncidents = async () => {
    const response = await axios.get("http://localhost:8001/search", {
      params: { query, severity, attack_type: attackType, page, size: 10 },
    });
    setResults(response.data.results);
    setTotalPages(response.data.total_pages);
  };

  const handlePageChange = (newPage) => {
    setPage(newPage);
    searchIncidents();
  };

  return (
    <div className="app-container">
      <h1 className="header">Incident Search Dashboard</h1>
      <div className="filters-container">
        <input
          type="text"
          placeholder="Search title"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="input-field"
        />
        <select
          onChange={(e) => setSeverity(e.target.value)}
          className="dropdown"
        >
          <option value="">All Severities</option>
          <option value="high">High</option>
          <option value="medium">Medium</option>
          <option value="low">Low</option>
        </select>
        <select
          onChange={(e) => setAttackType(e.target.value)}
          className="dropdown"
        >
          <option value="">All Attack Types</option>
          <option value="Unauthorized Access">Unauthorized Access</option>
          <option value="Phishing">Phishing</option>
        </select>
        <button onClick={searchIncidents} className="search-button">
          Search
        </button>
      </div>

      <ul className="results-list">
        {results.map((result, index) => (
          <li key={index} className="result-item">
            <strong>{result.title}</strong>: {result.description}
          </li>
        ))}
      </ul>

      <div className="pagination">
        <button
          onClick={() => handlePageChange(page - 1)}
          disabled={page === 1}
          className="pagination-button"
        >
          Previous
        </button>
        <span className="page-info">
          Page {page} of {totalPages}
        </span>
        <button
          onClick={() => handlePageChange(page + 1)}
          disabled={page === totalPages}
          className="pagination-button"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default App;
