import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [query, setQuery] = useState("");
  const [severity, setSeverity] = useState("");
  const [attackType, setAttackType] = useState("");
  const [status, setStatus] = useState("");
  const [results, setResults] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [newIncident, setNewIncident] = useState({
    title: "",
    description: "",
    severity: "",
    attack_type: "",
    status: "Open",
  });

  const [incidentIdToUpdate, setIncidentIdToUpdate] = useState("");
  const [updatedIncident, setUpdatedIncident] = useState({
    title: "",
    description: "",
    severity: "",
    attack_type: "",
    status: "",
  });

  useEffect(() => {
    document.title = "Incident Dashboard - Manage Incidents";
  }, []);

  useEffect(() => {
    searchIncidents();
  }, [page]);

  const searchIncidents = async () => {
    const response = await axios.get("http://localhost:8001/search", {
      params: { query, severity, attack_type: attackType, status, page, size: 10 },
    });
    setResults(response.data.results);
    setTotalPages(response.data.total_pages);
  };

  const handleAddIncident = async () => {
    if (!newIncident.title || !newIncident.description || !newIncident.severity || !newIncident.attack_type) {
      alert("All fields are required!");
      return;
    }

    const response = await axios.post("http://localhost:8001/incidents", newIncident);
    alert("Incident added: " + response.data.title);
    setNewIncident({ title: "", description: "", severity: "", attack_type: "", status: "Open" });
    searchIncidents();
  };

  const handleUpdateIncident = async () => {
    const response = await axios.put(`http://localhost:8001/incidents/${incidentIdToUpdate}`, updatedIncident);
    alert("Incident updated: " + response.data.title);
    setUpdatedIncident({ title: "", description: "", severity: "", attack_type: "", status: "" });
    setIncidentIdToUpdate("");
    searchIncidents();
  };

  const handleUpdateStatus = async (id, status) => {
    const response = await axios.put(`http://localhost:8001/incidents/${id}/status`, { status });
    alert(`Incident status updated to: ${status}`);
    searchIncidents();
  };

  const handleDeleteIncident = async (id) => {
    try {
      await axios.delete(`http://localhost:8001/incidents/${id}`);
      alert("Incident deleted");
      searchIncidents();
    } catch (error) {
      alert("Error deleting incident");
    }
  };

  return (
    <div className="container">
      <h1>Incident Dashboard</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for incidents"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <select
          value={severity}
          onChange={(e) => setSeverity(e.target.value)}
        >
          <option value="">Severity</option>
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <select
          value={attackType}
          onChange={(e) => setAttackType(e.target.value)}
        >
          <option value="">Attack Type</option>
          <option value="Phishing">Phishing</option>
          <option value="Malware">Malware</option>
          <option value="DDoS">DDoS</option>
        </select>
        <button onClick={searchIncidents}>Search</button>
      </div>
      <div className="results">
        <h2>Incident List</h2>
        {results.length === 0 ? (
          <p>No incidents found</p>
        ) : (
          results.map((incident) => (
            <div className="incident" key={incident.id}>
              <h3>{incident.title}</h3>
              <p>{incident.description}</p>
              <p>Severity: {incident.severity}</p>
              <p>Attack Type: {incident.attack_type}</p>
              <p>Status: {incident.status}</p>
              <button onClick={() => handleUpdateStatus(incident.id, "Resolved")}>Mark as Resolved</button>
              <button onClick={() => handleDeleteIncident(incident.id)}>Delete</button>
            </div>
          ))
        )}
      </div>
      <div className="pagination">
        <button onClick={() => setPage(page - 1)} disabled={page === 1}>
          Prev
        </button>
        <button onClick={() => setPage(page + 1)} disabled={page === totalPages}>
          Next
        </button>
      </div>
      <div className="add-incident-form">
        <h2>Add New Incident</h2>
        <input
          type="text"
          placeholder="Title"
          value={newIncident.title}
          onChange={(e) => setNewIncident({ ...newIncident, title: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newIncident.description}
          onChange={(e) => setNewIncident({ ...newIncident, description: e.target.value })}
        />
        <select
          value={newIncident.severity}
          onChange={(e) => setNewIncident({ ...newIncident, severity: e.target.value })}
        >
          <option value="">Severity</option>
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <select
          value={newIncident.attack_type}
          onChange={(e) => setNewIncident({ ...newIncident, attack_type: e.target.value })}
        >
          <option value="">Attack Type</option>
          <option value="Phishing">Phishing</option>
          <option value="Malware">Malware</option>
          <option value="DDoS">DDoS</option>
        </select>
        <button onClick={handleAddIncident}>Add Incident</button>
      </div>
    </div>
  );
};

export default App;
