import React, { useState } from "react";
import axios from "axios";

const App = () => {
    const [query, setQuery] = useState("");
    const [severity, setSeverity] = useState("");
    const [attackType, setAttackType] = useState("");
    const [results, setResults] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalResults, setTotalResults] = useState(0);
    const resultsPerPage = 10; // Change this if you want more results per page

    const searchIncidents = async () => {
        const response = await axios.get("http://localhost:8001/search", {
            params: {
                query,
                severity,
                attack_type: attackType,
                page: currentPage,
                size: resultsPerPage,
            },
        });
        setResults(response.data.results);
        setTotalResults(response.data.total); // Assuming total is being returned from the backend
    };

    const handleNextPage = () => {
        if (currentPage * resultsPerPage < totalResults) {
            setCurrentPage(currentPage + 1);
        }
    };

    const handlePrevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    return (
        <div>
            <h1>Incident Search Dashboard</h1>
            <input
                type="text"
                placeholder="Search title"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
            />
            <select onChange={(e) => setSeverity(e.target.value)}>
                <option value="">All Severities</option>
                <option value="high">High</option>
                <option value="medium">Medium</option>
                <option value="low">Low</option>
            </select>
            <select onChange={(e) => setAttackType(e.target.value)}>
                <option value="">All Attack Types</option>
                <option value="Unauthorized Access">Unauthorized Access</option>
                <option value="Phishing">Phishing</option>
            </select>
            <button onClick={searchIncidents}>Search</button>
            <ul>
                {results.map((result, index) => (
                    <li key={index}>
                        <strong>{result.title}</strong>: {result.description}
                    </li>
                ))}
            </ul>
            <div>
                <button onClick={handlePrevPage} disabled={currentPage === 1}>
                    Previous
                </button>
                <span> Page {currentPage} </span>
                <button
                    onClick={handleNextPage}
                    disabled={currentPage * resultsPerPage >= totalResults}
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default App;
