import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [query, setQuery] = useState("");
  const [severity, setSeverity] = useState("");
  const [attackType, setAttackType] = useState("");
  const [status, setStatus] = useState(""); // New state for status filter
  const [results, setResults] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [newIncident, setNewIncident] = useState({
    title: "",
    description: "",
    severity: "",
    attack_type: "",
    status: "Open", // Default status
  });
  const [incidentIdToUpdate, setIncidentIdToUpdate] = useState("");
  const [updatedIncident, setUpdatedIncident] = useState({
    title: "",
    description: "",
    severity: "",
    attack_type: "",
    status: "Open",
  });

  useEffect(() => {
    searchIncidents();
  }, [page]);

  const searchIncidents = async () => {
    const response = await axios.get("http://localhost:8001/search", {
      params: { query, severity, attack_type: attackType, status, page, size: 10 },
    });
    setResults(response.data.results);
    setTotalPages(response.data.total_pages);
  };

  const handleAddIncident = async () => {
    // Validation for required fields
    if (!newIncident.title || !newIncident.description || !newIncident.severity || !newIncident.attack_type) {
      alert("All fields are required!");
      return;
    }

    const response = await axios.post("http://localhost:8001/incidents", newIncident);
    alert("Incident added: " + response.data.title);
    setNewIncident({ title: "", description: "", severity: "", attack_type: "", status: "Open" });
    searchIncidents();
  };

  const handleUpdateIncident = async () => {
    const response = await axios.put(`http://localhost:8001/incidents/${incidentIdToUpdate}`, updatedIncident);
    alert("Incident updated: " + response.data.title);
    setUpdatedIncident({ title: "", description: "", severity: "", attack_type: "", status: "Open" });
    setIncidentIdToUpdate("");
    searchIncidents();
  };

  const handleDeleteIncident = async (id) => {
    try {
        await axios.delete(`http://localhost:8001/incidents/${id}`);
        alert("Incident deleted.");
        searchIncidents(); // Refresh the list of incidents
    } catch (error) {
        console.error("Error deleting incident:", error);
        alert("Failed to delete incident.");
    }
};

  return (
    <div className="app-container">
      <h1 className="app-title">Incident Dashboard</h1>

      <div className="filters-container">
        <input
          className="search-input"
          type="text"
          placeholder="Search title"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <select className="dropdown" onChange={(e) => setSeverity(e.target.value)}>
          <option value="">All Severities</option>
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Critical">Critical</option>
        </select>
        <select className="dropdown" onChange={(e) => setAttackType(e.target.value)}>
            <option value="">All Attack Types</option>
            <option value="Unauthorized Access">Unauthorized Access</option>
            <option value="SQL Injection">SQL Injection</option>
            <option value="DDoS">DDoS</option>
        </select>
        <select className="dropdown" onChange={(e) => setStatus(e.target.value)}>
            <option value="">All Statuses</option>
            <option value="Open">Open</option>
            <option value="Closed">Closed</option>
            <option value="In Progress">In Progress</option>
        </select>
      </div>

      <div className="results-container">
        {results.map((incident) => (
          <div key={incident.id} className="incident-card">
            <h2>{incident.title}</h2>
            <p>{incident.description}</p>
            <p>Severity: {incident.severity}</p>
            <p>Attack Type: {incident.attack_type}</p>
            <p>Status: {incident.status}</p>
            <button onClick={() => handleDeleteIncident(incident.id)}>Delete</button>
          </div>
        ))}
      </div>

      <div className="pagination">
        <button onClick={() => setPage(Math.max(1, page - 1))} disabled={page === 1}>
          Previous
        </button>
        <span>{page} of {totalPages}</span>
        <button onClick={() => setPage(Math.min(totalPages, page + 1))} disabled={page === totalPages}>
          Next
        </button>
      </div>

      <div className="add-incident">
        <h2>Add New Incident</h2>
        <input
          type="text"
          placeholder="Title"
          value={newIncident.title}
          onChange={(e) => setNewIncident({ ...newIncident, title: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newIncident.description}
          onChange={(e) => setNewIncident({ ...newIncident, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Severity"
          value={newIncident.severity}
          onChange={(e) => setNewIncident({ ...newIncident, severity: e.target.value })}
        />
        <input
          type="text"
          placeholder="Attack Type"
          value={newIncident.attack_type}
          onChange={(e) => setNewIncident({ ...newIncident, attack_type: e.target.value })}
        />
        <button onClick={handleAddIncident}>Add Incident</button>
      </div>

      <div className="update-incident">
        <h2>Update Incident</h2>
        <input
          type="text"
          placeholder="Incident ID"
          value={incidentIdToUpdate}
          onChange={(e) => setIncidentIdToUpdate(e.target.value)}
        />
        <input
          type="text"
          placeholder="Title"
          value={updatedIncident.title}
          onChange={(e) => setUpdatedIncident({ ...updatedIncident, title: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={updatedIncident.description}
          onChange={(e) => setUpdatedIncident({ ...updatedIncident, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Severity"
          value={updatedIncident.severity}
          onChange={(e) => setUpdatedIncident({ ...updatedIncident, severity: e.target.value })}
        />
        <input
          type="text"
          placeholder="Attack Type"
          value={updatedIncident.attack_type}
          onChange={(e) => setUpdatedIncident({ ...updatedIncident, attack_type: e.target.value })}
        />
        <input
          type="text"
          placeholder="Status"
          value={updatedIncident.status}
          onChange={(e) => setUpdatedIncident({ ...updatedIncident, status: e.target.value })}
        />
        <button onClick={handleUpdateIncident}>Update Incident</button>
      </div>
    </div>
  );
};

export default App;
